            }
            return one_over_n;
        },
        all_vertices);

    // æ€»è¿­ä»£è¿‡ç¨‹
    double d = (double)0.85;
        for (int ii = 0;ii < num_iterations;ii ++) {
        printf("delta(%d)=%lf\n", ii, delta);
